export function ProviderLogos() {
  return (
    <div className="w-full overflow-hidden">
      {/* Header text */}
      <p className="text-center text-zinc-500 text-sm mb-8 tracking-wide uppercase font-medium">
        Detectamos credenciales de +40 proveedores
      </p>

      {/* Marquee container */}
      <div className="relative">
        {/* Gradient overlays for fade effect */}
        <div className="absolute left-0 top-0 bottom-0 w-24 bg-gradient-to-r from-[#09090b] to-transparent z-10 pointer-events-none" />
        <div className="absolute right-0 top-0 bottom-0 w-24 bg-gradient-to-l from-[#09090b] to-transparent z-10 pointer-events-none" />

        {/* Marquee track */}
        <div className="flex animate-marquee">
          {/* First set */}
          {providers.map((provider) => (
            <div
              key={provider.name}
              className="flex items-center gap-3 px-6 py-3 mx-3 rounded-xl border border-white/5 bg-white/[0.02] hover:bg-white/[0.05] hover:border-white/10 transition-all duration-300 cursor-default group"
            >
              <div
                className="logo-item group-hover:opacity-100 transition-all duration-300"
                style={{ color: provider.color }}
              >
                <provider.Logo />
              </div>
              <span className="text-sm font-medium text-zinc-400 group-hover:text-zinc-200 transition-colors whitespace-nowrap">
                {provider.name}
              </span>
            </div>
          ))}
          {/* Duplicate for seamless loop */}
          {providers.map((provider) => (
            <div
              key={`${provider.name}-dup`}
              className="flex items-center gap-3 px-6 py-3 mx-3 rounded-xl border border-white/5 bg-white/[0.02] hover:bg-white/[0.05] hover:border-white/10 transition-all duration-300 cursor-default group"
            >
              <div
                className="logo-item group-hover:opacity-100 transition-all duration-300"
                style={{ color: provider.color }}
              >
                <provider.Logo />
              </div>
              <span className="text-sm font-medium text-zinc-400 group-hover:text-zinc-200 transition-colors whitespace-nowrap">
                {provider.name}
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Additional text */}
      <p className="text-center text-zinc-600 text-xs mt-6">
        + MongoDB, PostgreSQL, MySQL, Redis, Azure, NPM, Mapbox, Discord, SendGrid, Sentry y muchos m√°s
      </p>
    </div>
  );
}
